import React from 'react'
import './App.css';
import SearchBar from '../SearchBar/SearchBar'; 
import SearchResults from '../SearchResults/SearchResults';
import Playlist from '../Playlist/Playlist';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: [{name: 'name7', artist: 'artist7', id: 7, album: 'album7' }, {name: 'name2', artist: 'artist2', id: 2, album: 'album2' }, {name: 'name3', artist: 'artist3', id: 3, album: 'album3'}],
      playlistTracks: [{name: 'plname7', artist: 'plartist7', id: 'pl7', album: 'plalbum7' }, {name: 'plname2', artist: 'plartist2', id: 'pl2', album: 'plalbum2' }, {name: 'plname3', artist: 'artist3', id: 'pl3', album: 'plalbum3'}],
      playlistName: 'favorites'
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack
  }

  addTrack (newTrack) {
    let list = this.state.playlistTracks;
    if (list.find(savedTrack => savedTrack.id === newTrack.id)) {
      return;
    }
    list.push(newTrack) 
    this.setState(
      {playListTracks: list}
      )  
  }

  removeTrack(toRemove) {
    let currentPlayList = this.state.playlistTracks; 
    let newPlayList = currentPlayList.filter(track => track.id !== toRemove )
    this.setState(
      {playListTracks: newPlayList}
      )
  }

  render() {
    return (
      <div>
          <h1>Ja<span className="highlight">mmm</span>ing</h1>
          <div className="App">
              <SearchBar />
              <div className="App-playlist">
              <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}  />
              <Playlist playlistTracks={this.state.playlistTracks} playlistName={this.state.playlistName} />
              </div>
          </div>
      </div>
    );
  }
}
export default App;
