import SearchBar from "../components/SearchBar/SearchBar";

let accessToken = '';
const clientId = '734956a8c8904f718a0bcbd975ff15f4';
const redirectUri = 'http://localhost:3000/'; 

const Spotify = {
    getAccessToken() {
        if (accessToken) {
            return accessToken;
        }
        const AccessTokenMatch = window.location.href.match(/access_token=([^&]*)/);
        const expiresInMatch = window.location.href.match(/expires_in=([^&]*)/);

        if(AccessTokenMatch && expiresInMatch) {
            accessToken = AccessTokenMatch[1];
            let expiresIn = Number(expiresInMatch[1]);
            window.setTimeout(() => accessToken = '', expiresIn * 1000);
            window.history.pushState('Access Token', null, '/');
            return accessToken;
        } else {
            const accessUrl= `https://accounts.spotify.com/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&scope=user-read-private%20user-read-email&response_type=token&state=123`
            window.location = accessUrl;
        }
    },

    search (term) {
        const accessToken = Spotify.getAccessToken();
        return fetch(`https://api.spotify.com/v1/search?type=track&q=${term}`, {
            headers: {
                Authorization: `Bearer ${accessToken}`
            }
        }).then(response => {
            return response.json;
        }).then(jsonResponse => {
            if(!jsonResponse.tracks) {
                return [];
            } else {
                return jsonResponse.tracks.map( track => {
                    return {
                        ID: track.id, 
                        Name: track.name,
                        Arist: track.artists[0],
                        Album: track.album.name,
                        URI: track.uri        
            }           
                })
            }
        })
    }   
};

export default Spotify;
